    public void incrementarColuna(double[] envio) { // to fix
        int temp = contador;
        for (int i = contador; i < envio.length; i++, temp += 2) {
            tablePrincipal.getModel().setValueAt(envio[i], temp, colunaAtiva);

        }
        if (trigger == false) {
            trigger = true;
        } else {

            contador++;
        }
        colunaAtiva++;

    }

